sp_widget.client_script:
api.controller=function($scope, spUtil) {
  /* widget controller */
  var c = this;

	$scope.search = function(type){
		var query = document.getElementById('searchBox').value.toLowerCase();
		var i;
		switch(type){
			case 'species':
			if(query == ''){
				$scope.data.displaySpecies = $scope.data.species;
			} else{
					$scope.data.displaySpecies = [];
					for(i = 0; i < $scope.data.species.length; i++){
						if($scope.data.species[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.species[i].description.toLowerCase().indexOf(query) > -1){
							$scope.data.displaySpecies.push($scope.data.species[i]);
					}
				}
			}
			break;

			case 'career':
			if(query == ''){
				$scope.data.displayCareers = $scope.data.careers;
			} else{
				$scope.data.displayCareers = [];
				for(i = 0; i < $scope.data.careers.length; i++){
					if($scope.data.careers[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.careers[i].description.toLowerCase().indexOf(query) > -1){
						 $scope.data.displayCareers.push($scope.data.careers[i]);
					}
				}
			}
			break;

			case 'specialization':
			if(query == ''){
				$scope.data.displaySpecs = $scope.data.specializations;
			} else{
				$scope.data.displaySpecs = [];
				for(i = 0; i < $scope.data.specializations.length; i++){
					if($scope.data.specializations[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.specializations[i].description.toLowerCase().indexOf(query) > -1){
						 $scope.data.displaySpecs.push($scope.data.specializations[i]);
					}
				}
			}
			break;

			case 'skills':
			if(query == ''){
				$scope.data.displaySkills = $scope.data.skills;
			} else{
				$scope.data.displaySkills = [];
				for(i = 0; i < $scope.data.skills.length; i++){
					if($scope.data.skills[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.skills[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displaySkills.push($scope.data.skills[i]);
					}
				}
			}
			break;

			case 'talents':
			if(query == ''){
				$scope.data.displayTalents = $scope.data.talents;
			} else{
				$scope.data.displayTalents = [];
				for(i = 0; i < $scope.data.talents.length; i++){
					if($scope.data.talents[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.talents[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displayTalents.push($scope.data.talents[i]);
					}
				}
			}
			break;

			case 'gear':
			if(query == ''){
				$scope.data.displayGear = $scope.data.gear;
			} else{
				$scope.data.displayGear = [];
				for(i = 0; i < $scope.data.gear.length; i++){
					if($scope.data.gear[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.gear[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displayGear.push($scope.data.gear[i]);
					}
				}
			}
			break;

			case 'wq':
			if(query == ''){
				$scope.data.displayWeaponQualities = $scope.data.weaponQualities;
			} else{
				$scope.data.displayWeaponQualities = [];
				for(i = 0; i < $scope.data.weaponQualities.length; i++){
					if($scope.data.weaponQualities[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.weaponQualities[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displayWeaponQualities.push($scope.data.weaponQualities[i]);
					}
				}
			}
			break;

			case 'vehicles':
			if(query == ''){
				$scope.data.displayVehicles = $scope.data.vehicles;
			} else{
				$scope.data.displayVehicles = [];
				for(i = 0; i < $scope.data.vehicles.length; i++){
					if($scope.data.vehicles[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.vehicles[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displayVehicles.push($scope.data.vehicles[i]);
					}
				}
			}
			break;

			case 'galaxy':
			if(query == ''){
				$scope.data.displayGalaxy = $scope.data.galaxy;
			} else{
				$scope.data.displayGalaxy = [];
				for(i = 0; i < $scope.data.galaxy.length; i++){
					if($scope.data.galaxy[i].name.toLowerCase().indexOf(query) > -1 || $scope.data.galaxy[i].description.toLowerCase().indexOf(query) > -1){
						$scope.data.displayGalaxy.push($scope.data.galaxy[i]);
					}
				}
			}
			break;

			default:
				break;
		}

		if(query == '' && $scope.data.filter.section){
			$scope.filter($scope.data.filter.section, $scope.data.filter.filter);
		}
	}

	$scope.showSection = function(section){
		switch(section){
			case 'parent':
			$scope.data.showSection = 'parent';
			break;

			case 'Species':
			$scope.data.showSection = 'species';
			break;

			case 'Careers':
			$scope.data.showSection = 'careers';
			break;

			case 'Career Specializations':
			$scope.data.showSection = 'careerSpecializations';
			break;

			case 'Skills':
			$scope.data.showSection = 'skills';
			break;

			case 'Talents':
			$scope.data.showSection = 'talents';
			break;

			case 'Gear':
			$scope.data.showSection = 'gear';
			break;

			case 'Weapon Qualities':
			$scope.data.showSection = 'weaponQualities';
			break;

			case 'Vehicles':
			$scope.data.showSection = 'vehicles';
			break;

			case 'The Force':
			$scope.data.showSection = 'force';
			break;

			case 'The Galaxy':
			$scope.data.showSection = 'galaxy';
			break;

			default:
				break;
		}
	}

	$scope.showDetails = function(section, rec){
		$scope.data.currentRecord = rec;

		switch(section){
			case 'species':
			$scope.data.showDetails = 'species';
			break;

			case 'careers':
			$scope.data.showDetails = 'careers';
			break;

			case 'specs':
			$scope.data.showDetails = 'careerSpecs';
			break;

			case 'skills':
			$scope.data.showDetails = 'skills';
			break;

			case 'talents':
			$scope.data.showDetails = 'talents';
			break;

			case 'gear':
			$scope.data.showDetails = 'gear';
			break;

			case 'wq':
			$scope.data.showDetails = 'weaponQualities';
			break;

			case 'vehicle':
			$scope.data.showDetails = 'vehicle';
			break;

			case 'force':
			$scope.data.showDetails = 'force';
			break;

			case 'galaxy':
			$scope.data.showDetails = 'galaxy';
			break;

			default:
				break;
		}
	}

	$scope.hideDetails = function(){
		$scope.data.showDetails = '';
	}

	$scope.filter = function(section, filter){
		$scope.data.filter = {
			section: section,
			filter: filter
		};
		var i = 0;
		switch(section){
			case 'species':
			if(filter == 'All'){
				$scope.data.displaySpecies = $scope.data.species;
			} else{
				$scope.data.displaySpecies = [];
				for(i = 0; i < $scope.data.species.length; i++){
					if($scope.data.species[i].name.indexOf(filter) == 0){
						$scope.data.displaySpecies.push($scope.data.species[i]);
					}
				}
			}
			break;

			case 'careerSpec':
			$scope.data.displaySpecs = [];
			var career = document.getElementById('careerSpecFilter').value;
			if(career == 'all'){
				$scope.data.displaySpecs = $scope.data.specializations;
			} else{
				for(i = 0; i < $scope.data.specializations.length; i++){
					if($scope.data.specializations[i].career == career){
						 $scope.data.displaySpecs.push($scope.data.specializations[i]);
					}
				}
			}
			break;

			case 'skills':
			$scope.data.displaySkills = [];
			var type = document.getElementById('skillsFilter').value;
			if(type == 'all'){
				$scope.data.displaySkills = $scope.data.skills;
			} else{
				for(i = 0; i < $scope.data.skills.length; i++){
					if($scope.data.skills[i].type == type){
						$scope.data.displaySkills.push($scope.data.skills[i]);
					}
				}
			}
			break;

			case 'talents':
			if(filter == 'All'){
				$scope.data.displayTalents = $scope.data.talents;
			} else{
				$scope.data.displayTalents = [];
				for(i = 0; i < $scope.data.talents.length; i++){
					if($scope.data.talents[i].name.indexOf(filter) == 0){
						$scope.data.displayTalents.push($scope.data.talents[i]);
					}
				}
			}
			break;

			case 'gear':
			var type = document.getElementById('gearFilter').value;
			if(type == 'all'){
				$scope.data.displayGear = $scope.data.gear;
			} else{
				$scope.data.displayGear = [];
				for(i = 0; i < $scope.data.gear.length; i++){
					if($scope.data.gear[i].type == type){
						$scope.data.displayGear.push($scope.data.gear[i]);
					}
				}
			}
			break;

			case 'wq':
			if(filter == 'All'){
				$scope.data.displayWeaponQualities = $scope.data.weaponQualities;
			} else{
				$scope.data.displayWeaponQualities = [];
				for(i = 0; i < $scope.data.weaponQualities.length; i++){
					if($scope.data.weaponQualities[i].name.indexOf(filter) == 0){
						$scope.data.displayWeaponQualities.push($scope.data.weaponQualities[i]);
					}
				}
			}
			break;

			case 'vehicles':
			var type = document.getElementById('vehicleFilter').value;
			if(type == 'all'){
				$scope.data.displayVehicles = $scope.data.vehicles;
			} else{
				$scope.data.displayVehicles = [];
				for(i = 0; i < $scope.data.vehicles.length; i++){
					if($scope.data.vehicles[i].type == type){
						$scope.data.displayVehicles.push($scope.data.vehicles[i]);
					}
				}
			}
			break;

			case 'galaxy':
			var sector = document.getElementById('galaxyFilter').value;
			if(sector == 'all'){
				$scope.data.displayGalaxy = $scope.data.galaxy;
			} else{
				$scope.data.displayGalaxy = [];
				for(i = 0; i < $scope.data.galaxy.length; i++){
					if($scope.data.galaxy[i].sector == sector){
						$scope.data.displayGalaxy.push($scope.data.galaxy[i]);
					}
				}
			}
			break;

			default:
				break;
		}
	}
};

