	$scope.removeVehicle = function(vehicle){
		if(confirm('Are you sure you want to remove this ship?')){
			c.server.get({action: 'removeVehicle', vehicle: vehicle}).then(function(response){
				c.server.refresh();
			});
		}
	}

};

