sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
//retrieve caharacter details and pass to data object.
var characterID = $sp.getParameter('character_id');
	data.characterID = characterID;
	data.playerID = gs.getUserID();
var charGR = new GlideRecord('x_176383_star_wars_character');
		charGR.get(characterID);
	if(data.playerID == charGR.player.sys_id || gs.hasRole('admin')){
		data.canEdit = true;
	} else{
		data.canEdit = false;
	}
		data.character = {
			name: charGR.name + '' || 'Name your character',
			species: charGR.species.getDisplayValue(),
			speciesDescription: charGR.species.description + '',
			career: charGR.career.getDisplayValue(),
			careerID: charGR.career.sys_id + '',
			specializationIDs: charGR.career_specializations.toString(),
			specializationNames: '',
			brawn: charGR.brawn + '',
			agility: charGR.agility + '',
			intellect: charGR.intellect + '',
			cunning: charGR.cunning + '',
			willpower: charGR.willpower + '',
			presence: charGR.presence + '',
			availableXPInt: parseInt(charGR.available_xp),
			availableXPString: charGR.available_xp + '',
			totalXPInt: parseInt(charGR.total_xp),
			totalXPString: charGR.total_xp + '',
			strainCurrentInt: parseInt(charGR.strain_current),
			strainCurrentString: charGR.strain_current + '',
			strainThresholdInt: parseInt(charGR.strain_threshold),
			strainThresholdString: charGR.strain_threshold + '',
			woundCurrentInt: parseInt(charGR.wounds_current),
			woundCurrentString: charGR.wounds_current + '',
			woundThresholdInt: parseInt(charGR.wounds_threshold),
			woundThresholdString: charGR.wounds_threshold + '',
			soakInt: parseInt(charGR.soak_value),
			soakString: charGR.soak_value + '',
			rangedDefenseInt: parseInt(charGR.defense_ranged),
			rangedDefenseString: charGR.defense_ranged + '',
			meleeDefenseInt: parseInt(charGR.defense_melee),
			meleeDefenseString: charGR.defense_melee + '',
			creditsInt: parseInt(charGR.credits),
			creditsString: charGR.credits + '',
			age: charGR.age + '',
			eyes: charGR.eyes + '',
			hair: charGR.hair + '',
			gender: charGR.gender + '',
			height: charGR.height + '',
			weight: charGR.weight + '',
			criticalInjuriesInt: parseInt(charGR.critical_injuries),
			criticalInjuriesString: charGR.critical_injuries + '',
			forceRatingInt: parseInt(charGR.force_rating),
			forceRatingString: charGR.force_rating + '',
			background: charGR.description_html + '',
			notableFeatures: charGR.notable_features + '',
			notes: charGR.notes + '',
			otherGear: charGR.other_gear + ''
		};

	//retrieve career info to display when info button clicked.
	data.careerInfo = {
		name:charGR.career.getDisplayValue(),
		description:charGR.career.description + ''
	};

	//retrieve career specialization display values
	data.specializationInfo = [];
	data.availableSpecs = [];
	var spec = new GlideRecord('x_176383_star_wars_career_specialization');
	//spec.addQuery('sys_id', 'IN', data.character.specializationIDs);
	//spec.addQuery('career', data.character.careerID).addOrCondition('career', '');
	spec.orderBy('name');
	spec.query();
	while(spec.next()){
		if(data.character.specializationIDs.indexOf(spec.sys_id.toString()) > -1){
			var specObj = {
				name: spec.name + '',
				description: spec.description + ''
			};
			data.specializationInfo.push(specObj);
			if(data.character.specializationNames == ''){
				data.character.specializationNames = spec.getDisplayValue();
			} else{
				data.character.specializationNames += ', ' + spec.getDisplayValue();
			}
		} else{
			var spObj = {
				name: spec.getDisplayValue(),
				sysID: spec.sys_id + '',
				careerID: spec.career.sys_id + '',
				careerName: spec.career.getDisplayValue(),
				costToPurchase: (data.character.specializationIDs.split(',').length + 1) * 10,
				careerSkills: spec.specialization_skills + '',
				description: spec.description + ''
			};
			if(spec.career.sys_id.toString() != data.character.careerID){
				 spObj.costToPurchase += 10;
			}
			if(spObj.costToPurchase <= data.character.availableXPInt){
				spObj.canPurchase = true;
			} else{
				spObj.canPurchase = false;
			}
			data.availableSpecs.push(spObj);
		}
	}

	//set background image
	//remove the db_image check once all images are loaded
	var imgCheck = data.character.career.toLowerCase() + '.png';
	var img = new GlideRecord('db_image');
	img.addQuery('name', imgCheck);
	img.query();
	if(img.next()){
		data.backgroundURL = '/' + img.name;
	} else{
		data.backgroundURL = '/sw_space.jpg';
	}

	//retrieve character skills and values
	data.skills = [];
	var skills = new GlideRecord('x_176383_star_wars_m2m_character_skills');
	skills.addQuery('character', characterID);
	skills.orderBy('skill.name');
	skills.query();
	while(skills.next()){
		var rec = {
			name: skills.skill.name + '',
			rankInt: parseInt(skills.rank),
			rankString: skills.rank + '',
			costToNextRank: parseInt(skills.cost_to_next_rank),
			isCareer: skills.career + '',
			description: skills.skill.description + '',
			sysID: skills.sys_id + ''
		};
		data.skills.push(rec);
	}

	//retrieve talents
	data.talents = [];
	var purchasedTalentIDs = [];
	var talent = new GlideRecord('x_176383_star_wars_character_talents_m2m');
	talent.addQuery('character', characterID);
	talent.orderBy('talent.talent.name');
	talent.query();
	var index = 0;
	while(talent.next()){
		purchasedTalentIDs.push(talent.talent.sys_id + '');
		if(index > 0 && data.talents[index - 1].name == talent.talent.talent.getDisplayValue()){
			data.talents[index - 1].ranks = (parseInt(data.talents[index - 1].ranks) + 1) + '';
		} else{
			var rec = {
					name: talent.talent.talent.getDisplayValue(),
					ranks: '1',
					description: talent.talent.talent.description + '',
					forceTalent: talent.talent.talent.force_talent + '',
					passiveActive: talent.talent.talent.passive_or_active + '',
					tier: talent.talent.rank + '',
					careerSpec: talent.talent.career_specialization.sys_id + ''
				};
			data.talents.push(rec);
			index++;
		}
	}
	//retrieve talent tree
	//retrieve talent trees for specializations
	data.availableTalents = [];
	var talentTree = new GlideRecord('x_176383_star_wars_career_specialization_talent_tree_m2m');
	talentTree.addQuery('career_specialization', 'IN', data.character.specializationIDs);
	talentTree.orderBy('career_specialization');
	talentTree.orderBy('rank');
	talentTree.orderBy('talent.name');
	talentTree.query();
	while(talentTree.next()){
		var t = {
			sysID: talentTree.sys_id + '',
			name: talentTree.talent.getDisplayValue(),
			tier: parseInt(talentTree.rank),
			tierString: talentTree.rank + '',
			specID: talentTree.career_specialization.sys_id + '',
			specName: talentTree.career_specialization.getDisplayValue(),
			costToPurchase: parseInt(talentTree.xp_cost),
			description: talentTree.talent.description + '',
			activeOrPassive: talentTree.talent.passive_or_active.getDisplayValue(),
			forceTalent: talentTree.talent.force_talent + '',
			preReqs: talentTree.pre_requisite_talents.toString().split(','),
			isPurchased: false
		};
		if((t.tier == 1 || t.preReqs[0] == '') && t.costToPurchase <= data.character.availableXPInt){
			 t.canPurchase = true;
		}
		data.availableTalents.push(t);
	}
	for(var i = 0; i < data.availableTalents.length; i++){
		for(var j = 0; j < purchasedTalentIDs.length; j++){
			if(data.availableTalents[i].preReqs.join().indexOf(purchasedTalentIDs[j]) >= 0 && data.character.availableXPInt >= data.availableTalents[i].costToPurchase){
				 data.availableTalents[i].canPurchase = true;
			}
			if(data.availableTalents[i].sysID == purchasedTalentIDs[j]){
				data.availableTalents[i].isPurchased = true;
			}
		}
	}

	//retrieve inventory list
	data.availableGear = [];
	var gear = new GlideRecord('x_176383_star_wars_gear');
	gear.addQuery('type', '!=', 'vehicle_weapon').addCondition('type', '!=', 'vehicle_mod');
	gear.orderBy('type');
	gear.orderBy('name');
	gear.query();
	while(gear.next()){
		var g = {
			name: gear.name + '',
			encumbrance: gear.encumbrance + '',
			rarity: gear.rarity + '',
			type: gear.type.getDisplayValue(),
			typeID: gear.type + '',
			skillUsed: gear.skill_used.getDisplayValue(),
			price: gear.price + '',
			defense: gear.defense + '',
			soak: gear.soak + '',
			damage: gear.base_damage + '',
			range: gear.range.getDisplayValue(),
			hardPoints: gear.hard_points + '',
			description: gear.description_html + '',
			crit: gear.critical_rating + '',
			hpRequired: gear.hp_required + '',
			sysID: gear.sys_id + ''
		};
		data.availableGear.push(g);
	}
	data.availableGearAll = data.availableGear;

	//retrieve current character gear
	data.characterGear = [];
	var cg = new GlideRecord('x_176383_star_wars_character_gear_m2m');
	cg.addQuery('character', characterID);
	//cg.orderBy('item.type');
	cg.orderBy('item.name');
	cg.query();
	while(cg.next()){
		var cgObj = {
			name: cg.item.getDisplayValue(),
			quantity: cg.quantity + '',
			location: cg.location.getDisplayValue(),
			locValue: cg.location + '',
			damage: cg.damage + '',
			crit: cg.critical_rating + '',
			defense: cg.defense + '',
			soak: cg.soak + '',
			encumbrance: cg.item.encumbrance + '',
			type: cg.item.type.getDisplayValue(),
			typeID: cg.item.type + '',
			skillUsed: cg.item.skill_used.getDisplayValue(),
			range: cg.range.getDisplayValue(),
			rangeID: cg.range + '',
			hardPoints: cg.hard_points + '',
			hpRequired: cg.item.hp_required + '',
			description: cg.item.description_html + '',
			sysID: cg.sys_id + '',
			iconID: cg.sys_id + 'Chevron'
		};
		var wq = new GlideRecord('x_176383_star_wars_character_gear_special');
		wq.addQuery('character_gear', cg.sys_id);
		wq.orderBy('weapon_quality.name');
		wq.query();
		var qualityString = '';
		while(wq.next()){
			if(qualityString == ''){
				qualityString = wq.weapon_quality.getDisplayValue() + ': ' + wq.rank;
			} else{
				qualityString += ', ' + wq.weapon_quality.getDisplayValue() + ': ' + wq.rank;
			}
		}
		cgObj.weaponQualities = qualityString;
		data.characterGear.push(cgObj);
	}

	//gear types
	data.gearTypes = [];
	var choiceGR = new GlideRecord('sys_choice');
	choiceGR.addQuery('name', 'x_176383_star_wars_gear').addCondition('element', 'type').addCondition('value', 'DOES NOT CONTAIN', 'vehicle');
	choiceGR.orderBy('label');
	choiceGR.query();
	while(choiceGR.next()){
		var choiceObj = {
			name: choiceGR.label + '',
			value: choiceGR.value + ''
		};
		data.gearTypes.push(choiceObj);
	}

	//retrieve list of vehicles
	data.vehicles = [];
	data.vehiclesAll = [];
	var vehicle = new GlideRecord('x_176383_star_wars_vehicle');
	vehicle.orderBy('type');
	vehicle.orderBy('ship_name');
	vehicle.query();
	while(vehicle.next()){
		var vObj = {
			shipName: vehicle.ship_name + '',
			makeModel: vehicle.make_model + '',
			type: vehicle.type.getDisplayValue(),
			typeID: vehicle.type + '',
			sysID: vehicle.sys_id + '',
			price: vehicle.price + '',
			silhouette: vehicle.silhouette + '',
			hp: vehicle.hard_points + '',
			handling: vehicle.handling + '',
			speed: vehicle.speed + '',
			armor: vehicle.armor + '',
			defenseFore: vehicle.defense_fore + '',
			defenseAft: vehicle.defense_aft + '',
			defensePort: vehicle.defense_port + '',
			defenseStarboard: vehicle.defense_starboard + '',
			hullThreshold: vehicle.hull_trauma_threshold + '',
			ssThreshold: vehicle.system_strain_threshold + '',
			maximumAltitude: vehicle.maximum_altitude + '',
			sensorRange: vehicle.sensor_range.getDisplayValue(),
			description: vehicle.description + ''
		};
		if(vehicle.primary_hyperdrive_class == ''){
			vObj.hyperdrive = 'None';
		} else{
			vObj.hyperdrive = 'Primary Class: ' + vehicle.primary_hyperdrive_class + ', Backup Class: ' + vehicle.backup_hyperdrive_class;
		}

		if(vehicle.navicomputer == true){
			vObj.navicomputer = 'Yes';
		} else{
			vObj.navicomputer = 'No';
		}
		data.vehicles.push(vObj);
	}
	data.vehiclesAll = data.vehicles;
	//retrieve character vehicles
	data.characterVehicles = [];
	var cv = new GlideRecord('x_176383_star_wars_character_vehicle');
	cv.addQuery('character', characterID);
	cv.orderBy('name');
	cv.query();
	while(cv.next()){
		var v = {
			name: cv.name + '',
			vehicleName: cv.vehicle.ship_name + '',
			makeModel: cv.vehicle.make_model + '',
			sysID: cv.sys_id + ''
		};
		data.characterVehicles.push(v);
	}

	//retrieve force powers and enhancements
	data.characterPowers = [];
	var cForce = new GlideRecord('x_176383_star_wars_character_force_power');
	cForce.addQuery('character', data.characterID);
	cForce.orderBy('force_power.name');
	cForce.query();
	while(cForce.next()){
		var cForceObj = {
			name: cForce.force_power.getDisplayValue(),
			description: cForce.force_power.description + '',
			sysID: cForce.force_power.sys_id + ''
		};
		data.characterPowers.push(cForceObj);
	}

	data.cForceEnhancements = [];
	data.ownedEnhancements = [];
	var cfe = new GlideRecord('x_176383_star_wars_character_force_enhancements');
	cfe.addQuery('character', data.characterID);
	cfe.orderBy('character_force_power.force_power.name');
	cfe.orderBy('character_force_enhcancement.type');
	cfe.query();
	while(cfe.next()){
		if(data.cForceEnhancements.length == 0){
			var cfeObj = {
				type: cfe.character_force_enhcancement.type.getDisplayValue(),
				description: cfe.character_force_enhcancement.description + '',
				ranks: 1,
				forceID: cfe.character_force_power.force_power.sys_id + '',
				enhancementID: cfe.character_force_enhcancement.sys_id + ''
			};
			data.cForceEnhancements.push(cfeObj); 
		} else if(data.cForceEnhancements[data.cForceEnhancements.length - 1].type == cfe.character_force_enhcancement.type.getDisplayValue() &&  data.cForceEnhancements[data.cForceEnhancements.length - 1].description.toString() == cfe.character_force_enhcancement.description.toString()){
			data.cForceEnhancements[data.cForceEnhancements.length - 1].ranks++;
		} else{
			var cfeObj = {
				type: cfe.character_force_enhcancement.type.getDisplayValue(),
				description: cfe.character_force_enhcancement.description + '',
				ranks: 1,
				forceID: cfe.character_force_power.force_power.sys_id + '',
				enhancementID: cfe.character_force_enhcancement.sys_id + ''
			};
			data.cForceEnhancements.push(cfeObj);
		}
		data.ownedEnhancements.push(cfe.character_force_enhcancement.sys_id + '');
	}

		data.forcePowers = [];
	var force = new GlideRecord('x_176383_star_wars_force_power');
		force.orderBy('name');
		force.query();
		while(force.next()){
			if(JSON.stringify(data.characterPowers).indexOf(force.sys_id.toString()) == -1){
				var forceObj = {
					name: force.name + '',
					minRating: force.minimum_force_rating + '',
					costToPurchase: force.cost_to_purchase + '',
					description: force.description + '',
					isPurchased: false,
					canPurchase: false,
					sysID: force.sys_id + ''
				};
				if(parseInt(forceObj.costToPurchase) <= data.character.availableXPInt && parseInt(forceObj.minRating) <= data.character.forceRatingInt){
					forceObj.canPurchase = true;
				}
				data.forcePowers.push(forceObj);
			}
		}

		//retrieve force power enhancements
		data.forceEnhancements = [];
		var fe = new GlideRecord('x_176383_star_wars_force_power_enhancement');
		fe.orderBy('force_power.name');
		fe.orderBy('number');
		fe.query();
		while(fe.next()){
			if(JSON.stringify(data.ownedEnhancements).indexOf(fe.sys_id.toString()) == -1 && JSON.stringify(data.characterPowers).indexOf(fe.force_power.sys_id.toString()) > -1){
				var feObj = {
					type: fe.type.getDisplayValue(),
					preReqs: fe.pre_req_force_enhancements + '',
					forcePower: fe.force_power.getDisplayValue(),
					forcePowerID: fe.force_power.sys_id + '',
					costToPurchase: fe.cost_to_purchase + '',
					description: fe.description + '',
					canPurchase: false,
					isPurchased: false,
					sysID: fe.sys_id + ''
				};
				var preReqArray = feObj.preReqs.split(',');
				for(var i = 0; i < preReqArray.length; i++){
					if((preReqArray.length == 0 || JSON.stringify(data.ownedEnhancements).indexOf(preReqArray[i]) > -1) && parseInt(feObj.costToPurchase) <= data.character.availableXPInt){
						feObj.canPurchase = true;
					}
				}
				data.forceEnhancements.push(feObj);
			}
		}

	//process character updates
	if(input){
		switch(input.action){
			case 'saveCharacterDescription':
			charGR.gender = input.gender;
			charGR.age = input.age;
			charGR.eyes = input.eyes;
			charGR.height = input.height;
			charGR.weight = input.weight;
			charGR.hair = input.hair;
			charGR.notable_features = input.notableFeatures;
			charGR.description_html = input.background;
			charGR.notes = input.notes;
			charGR.update();
			data.character.gender = charGR.gender + '';
			data.character.age = charGR.age + '';
			data.character.eyes = charGR.eyes + '';
			data.character.height = charGR.height + '';
			data.character.weight = charGR.weight + '';
			data.character.hair = charGR.hair + '';
			data.character.notableFeatures = charGR.notable_features + '';
			data.character.background = charGR.description_html + '';
			data.character.notes = charGR.notes + '';
			break;

			case 'saveSoak':
			charGR.soak_value = input.soak;
			charGR.update();
			data.character.soakInt = parseInt(charGR.soak_value);
			data.character.soakString = charGR.soak_value + '';
			break;

			case 'saveWounds':
			charGR.wounds_threshold = input.threshold;
			charGR.wounds_current = input.current;
			charGR.update();
			data.character.woundThresholdInt = parseInt(charGR.wounds_threshold);
			data.character.woundThresholdString = charGR.wounds_threshold + '';
			data.character.woundCurrentInt = parseInt(charGR.wounds_current);
			data.character.woundCurrentString = charGR.wounds_current + '';
			break;

			case 'saveStrain':
			charGR.strain_threshold = input.threshold;
			charGR.strain_current = input.current;
			charGR.update();
			data.character.strainThresholdInt = parseInt(charGR.strain_threshold);
			data.character.strainThresholdString = charGR.strain_threshold + '';
			data.character.strainCurrentInt = parseInt(charGR.strain_current);
			data.character.strainCurrentString = charGR.strain_current + '';
			break;

			case 'saveDefense':
			charGR.defense_ranged = input.ranged;
			charGR.defense_melee = input.melee;
			charGR.update();
			data.character.rangedDefenseInt = parseInt(charGR.defense_ranged);
			data.character.rangedDefenseString = charGR.defense_ranged + '';
			data.character.meleeDefenseInt = parseInt(charGR.defense_melee);
			data.character.meleeDefenseString = charGR.defense_melee + '';	
			break;

			case 'saveCharacteristics':
			charGR.brawn = input.brawn;
			charGR.agility = input.agility;
			charGR.intellect = input.intellect;
			charGR.cunning = input.cunning;
			charGR.willpower = input.willpower;
			charGR.presence = input.presence;
			charGR.update();
			data.character.brawn = charGR.brawn + '';
			data.character.agility = charGR.agility + '';
			data.character.intellect = charGR.intellect + '';
			data.character.cunning = charGR.cunning + '';
			data.character.willpower = charGR.willpower + '';
			data.character.presence = charGR.presence + '';
			break;

			case 'saveName':
			charGR.name = input.name;
			charGR.update();
			data.character.name = charGR.name + '';
			break;

			case 'saveCredits':
			charGR.credits = input.credits;
			charGR.update();
			data.character.creditsInt = parseInt(charGR.credits);
			data.character.creditsString = data.character.creditsInt + '';
			break;

			case 'deleteGear':
			cg.get(input.sysID);
			cg.deleteRecord();
			for(var i = 0; i < data.characterGear.length; i++){
				if(data.characterGear[i].sysID == input.sysID){
					 data.characterGear.splice(i, 1);
				}
			}
			break;

			case 'purchaseItem':
			charGR.get(characterID);
			charGR.credits = data.character.creditsInt - parseInt(input.price);
			charGR.update();
			data.character.creditsInt = parseInt(charGR.credits);
			data.character.creditsString = data.character.creditsInt + '';
			cg = new GlideRecord('x_176383_star_wars_character_gear_m2m');
			cg.addQuery('item.name', input.item.name).addCondition('character', characterID);
			cg.query();
			if(cg.next()){
				cg.quantity = (parseInt(cg.quantity) + parseInt(input.quantity));
				cg.update();
				for(var i = 0; i < data.characterGear.length; i++){
					if(data.characterGear[i].sysID == cg.sys_id + ''){
						 data.characterGear[i].quantity = cg.quantity + '';
					}
				}
			} else{
				cg.initialize();
				cg.item = input.item.sysID;
				cg.character = characterID;
				cg.quantity = parseInt(input.quantity);
				cg.insert();
				var cgObj = {
					name: cg.item.getDisplayValue(),
					quantity: cg.quantity + '',
					damage: cg.item.base_damage + '',
					crit: cg.item.critical_rating + '',
					defense: cg.item.defense + '',
					soak: cg.item.soak + '',
					encumbrance: cg.item.encumbrance + '',
					type: cg.item.type.getDisplayValue(),
					typeID: cg.item.type + '',
					skillUsed: cg.item.skill_used.getDisplayValue(),
					range: cg.item.range.getDisplayValue(),
					hardPoints: cg.item.hard_points + '',
					description: cg.item.description + '',
					sysID: cg.sys_id + '',
					iconID: cg.sys_id + 'Chevron'
				};
				data.characterGear.push(cgObj);
			}
			gs.addInfoMessage(input.item.name + ' purchased');
			break;

			case 'purchaseTalent':
			talent.initialize();
			talent.talent = input.talentID;
			talent.character = characterID;
			talent.insert();
			charGR.get(characterID);
			charGR.available_xp = data.character.availableXPInt - parseInt(input.talent.costToPurchase);
			charGR.update();
			break;

			case 'saveSkill':
			var skill = new GlideRecord('x_176383_star_wars_m2m_character_skills');
			skill.get(input.skill.sysID + '');
			skill.rank = parseInt(input.skill.rankInt) + 1;
			var newCost = 0;
			if(input.skill.isCareer == 'true'){
				newCost = (parseInt(skill.rank) + 1) * 5;
			} else{
				newCost = ((parseInt(skill.rank) + 1) * 5) + 5;
			}
			skill.cost_to_next_rank = newCost;
			skill.update();
			data.character.availableXPInt -= parseInt(input.skill.costToNextRank);
			data.character.availableXPString = data.character.availableXPInt + '';
			charGR.get(characterID);
			charGR.available_xp = data.character.availableXPInt;
			charGR.update();
			for(var s = 0; s < data.skills.length; s++){
				if(data.skills[s].sysID == input.skill.sysID){
					data.skills[s].rankInt++;
					data.skills[s].rankString = data.skills[s].rankInt + '';
					data.skills[s].costToNextRank = newCost;
				}
			}
			break;

			case 'saveGearQuantity':
			var myGear = new GlideRecord('x_176383_star_wars_character_gear_m2m');
			myGear.get(input.gearID);
			myGear.quantity = input.quantity;
			myGear.update();
			data.currentGear = null;
			break;

			case 'saveGearEdit':
			var gedit = new GlideRecord('x_176383_star_wars_character_gear_m2m');
			gedit.get(input.gearID);
			gedit.critical_rating = input.crit;
			gedit.defense = input.defense;
			gedit.damage = input.damage;
			gedit.soak = input.soak;
			gedit.range = input.range;
			gedit.hard_points = input.hp;
			gedit.location = input.location;
			gedit.update();
			data.currentGear = null;
			break;

			case 'purchaseVehicle':
			var myShip = new GlideRecord('x_176383_star_wars_character_vehicle');
			myShip.initialize();
			myShip.character = characterID;
			myShip.vehicle = input.vehicleID;
			myShip.insert();
			break;

			case 'saveForceRating':
			charGR.force_rating = input.forceRating;
			charGR.update();
			break;

			case 'buyForcePower':
			cForce.initialize();
			cForce.character = data.characterID;
			cForce.force_power = input.power.sysID;
			cForce.insert();
			charGR.available_xp -= parseInt(input.power.costToPurchase);
			charGR.update();
			break;

			case 'buyForceEnhancement':
			var characterPower = new GlideRecord('x_176383_star_wars_character_force_power');
			characterPower.addQuery('character', data.characterID);
			characterPower.addQuery('force_power', input.enh.forcePowerID);
			characterPower.query();
			if(characterPower.next()){
				cfe.initialize();
				cfe.character = data.characterID;
				cfe.character_force_power = characterPower.sys_id + '';
				cfe.character_force_enhcancement = input.enh.sysID;
				cfe.insert();
				charGR.available_xp -= parseInt(input.enh.costToPurchase);
				charGR.update();
			}
			break;

			case 'buyNewSpec':
			var specArray = data.character.specializationIDs.split(',');
			specArray.push(input.spec.sysID);
			if(input.spec.careerSkills == '' && data.character.forceRatingInt == 0){
				 charGR.force_rating = 1;
			}
			charGR.career_specializations = specArray.join();
			charGR.available_xp -= input.spec.costToPurchase;
			charGR.update();
			var skillGR = new GlideRecord('x_176383_star_wars_m2m_character_skills');
			skillGR.addQuery('character', data.characterID).addCondition('skill', 'IN', input.spec.careerSkills);
			skillGR.query();
			while(skillGR.next()){
				skillGR.career = true;
				skillGR.cost_to_next_rank = (skillGR.rank + 1) * 5;
				skillGR.update();
			}
			break;

			case 'saveOtherGear':
			charGR.other_gear = input.gear;
			charGR.update();
			data.character.otherGear = charGR.other_gear + '';
			break;

			case 'removeVehicle':
			var characterVehicle = new GlideRecord('x_176383_star_wars_character_vehicle');
			characterVehicle.get(input.vehicle.sysID);
			characterVehicle.deleteRecord();
			break;

			case 'saveCrit':
			charGR.critical_injuries = parseInt(input.crits);
			charGR.update();
			data.character.criticalInjuriesInt = charGR.critical_injuries;
			data.character.criticalInjuriesString = data.character.criticalInjuriesInt + '';
			break;

			default:
				break;
		}
	}
})();
