sp_widget.client_script:
api.controller=function($scope, $location) {
  /* widget controller */
  var c = this;

	$scope.viewCharacterSheet = function(sysID){
		$location.search('id', 'star_wars_character_sheet');
		$location.search('character_id', sysID);
	}

	$scope.deleteCharacter = function(character){
		c.server.get({action: 'deleteCharacter', sysID: character}).then(function(response){
			c.server.refresh();
		});
	}
};

